/**
 * @module image/imagetextalternative/imagetextalternativeediting
 */

import ImagePropertiesCommand from './imagetextalternativecommand';
import Plugin from '@ckeditor/ckeditor5-core/src/plugin';

/**
 * The image properties editing plugin.
 *
 * Registers the `'imageProperties'` command.
 *
 * @extends module:core/plugin~Plugin
 */
export default class ImagePropertiesEditing extends Plugin {
	/**
	 * @inheritDoc
	 */
	init() {
		this.editor.commands.add( 'imageProperties', new ImagePropertiesCommand( this.editor ) );
	}
}
